tinymce.PluginManager.add('insertMath', function(editor, url) {
  // Add a button that opens a window
  editor.addButton('insertMath', {
    text: '插入公式',
    icon: false,
    onclick: function() {
      // Open window
      editor.windowManager.open({
        title: '編輯公式',
        body: [
          {type: 'textbox', name: 'title'}
        ],
        onsubmit: function(e) {
          editor.insertContent('$$' + e.data.title + '$$')
        }
      });

    }
  });
});
